import React, { useState } from 'react'

import { ExampleComponent } from 'usegstate'
import 'usegstate/dist/index.css'



const GStateContextProvider = React.createContext({}).Provider;

export const GStateProvider = ({children}:{children:JSX.Element}) => {
  const store = React.useRef({})
  const get = React.useCallback((a:string)=>{return store[a]},[])
  const set = React.useCallback((a:string,val:any)=>{store.current = {...store.current[a],...val}},[])
  return <GStateContextProvider value={{get,set}}>
    {children}
  </GStateContextProvider>
}

const App = () => {
  return <GStateProvider>
    <Client />
  </GStateProvider>
}

export default App
const Client = ()=>{
  const [ip,setIp] = useState('a');
  return <input value={ip} onChange={(e)=>setIp(e.target.value)}></input>
}
