    return store.current[a]},[])
  const set = React.useCallback((a:string,val:any)=>{


    console.log('[setb4]store',store)
    store.current = {...store.current[a],...val}},[])
    console.log('[set]store',store)
  return <GStateContextProvider value={{get,set}}>
    {children}
  </GStateContextProvider>
}

const App = () => {
  return <GStateProvider>
    <Client />
  </GStateProvider>
}

