import React, { useContext, useState } from 'react'

import { ExampleComponent } from 'usegstate'
import 'usegstate/dist/index.css'

const StoreContext = React.createContext<Partial<{get:()=>object,set:(a:string,v:object)=>void}>>({});

const GStateContextProvider = StoreContext.Provider;


const useStore = ()=>{
 return useContext(StoreContext);
//  const {get,set}  = useContext(StoreContext);
}

export const GStateProvider = ({children}:{children:JSX.Element}) => {
  const store = React.useRef({})
  const get = React.useCallback((a:string)=>{return store[a]},[])
  const set = React.useCallback((a:string,val:any)=>{store.current = {...store.current[a],...val}},[])
  return <GStateContextProvider value={{get,set}}>
    {children}
  </GStateContextProvider>
}

const App = () => {
  return <GStateProvider>
    <Client />
  </GStateProvider>
}

export default App
const Client = ()=>{
  const store = useStore();
  console.log(`store: `,store);
  const [ip,setIp] = useState('a');
  return<div> <input value={ip} onChange={(e)=>setIp(e.target.value)} autoFocus={true}></input></div>
}
